<?php$vtype = $this->session->userdata('vtype');$edit_data	   = $this->db->get_where('exam_fees' , array('ef_id' => $param2) )->result_array();$running_year  = $this->db->get_where('settings', array('type' => 'running_year'))->row()->description;$exams         = $this->db->get_where('exam', array('year' => $running_year, 'vtype'=>$vtype))->result_array();?><div class="tab-pane box active" id="edit" style="padding: 5px">    <div class="box-content">        <?php foreach($edit_data as $row):?>            <?php echo form_open(site_url('admin/payment_settings_action/update_exam_fee/'.$row['ef_id']), array('class' => 'form-horizontal form-groups-bordered validate','target'=>'_top'));?>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('class');?></label>                <div class="col-sm-6">                    <select name="class_id" class="form-control selectboxit class_id"                            onchange="return get_class_students(this.value)">                        <option value=""><?php echo get_phrase('select_class');?></option>                        <?php                        $classes = $this->db->get_where('class',['vtype'=>$vtype])->result_array();                        foreach ($classes as $cls):                            $selc = ($cls['class_id'] == $row['class_id']) ? 'selected' : '';                            ?>                            <option value="<?php echo $cls['class_id'];?>" <?php echo $selc;?>><?php echo $cls['name'];?></option>                        <?php endforeach;?>                    </select>                </div>            </div>            <div class="form-group">                <label class="col-sm-3 control-label">Exam Name</label>                <div class="col-sm-6">                    <select name="exam_id" class="form-control selectboxit class_id">                        <option value=""><?php echo get_phrase('select_exam');?></option>                        <?php                        foreach ($exams as $exam):                            $sel = ($exam['exam_id'] == $row['exam_id']) ? 'selected' : '';                            ?>                            <option value="<?php echo $exam['exam_id'];?>" <?php echo $sel;?>><?php echo $exam['name'];?></option>                        <?php endforeach;?>                    </select>                </div>            </div>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('exam_fee');?></label>                <div class="col-sm-6">                    <input type="text" class="form-control" name="exam_fee" value="<?php echo $row['exam_fee'];?>"                           data-validate="required" data-message-required="<?php echo get_phrase('value_required');?>"/>                </div>            </div>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('description');?></label>                <div class="col-sm-6">                    <input type="text" class="form-control" name="description" value="<?php echo $row['description'];?>"/>                </div>            </div>            <div class="form-group">                <div class="col-sm-offset-3 col-sm-5">                    <button type="submit" class="btn btn-info"><?php echo get_phrase('edit_exam_fee');?></button>                </div>            </div>            </form>        <?php endforeach;?>    </div></div>