<?php$vtype = $this->session->userdata('vtype');$edit_data = $this->db->get_where('session_fee_settings' , array('id' => $param2) )->result_array();?><div class="tab-pane box active" id="edit" style="padding: 5px">    <div class="box-content">        <?php foreach($edit_data as $row): ?>            <?php echo form_open(site_url('admin/payment_settings_action/update_session_fee/'.$row['id']), array('class' => 'form-horizontal form-groups-bordered validate','target'=>'_top'));?>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('class');?></label>                <div class="col-sm-6">                    <select name="class_id" class="form-control selectboxit class_id"                            onchange="return get_class_students(this.value)">                        <option value=""><?php echo get_phrase('select_class');?></option>                        <?php                        $classes = $this->db->get_where('class',['vtype'=>$vtype])->result_array();                        foreach ($classes as $cls):                            $selc = ($cls['class_id'] == $row['class_id']) ? 'selected' : '';                            ?>                            <option value="<?php echo $cls['class_id'];?>" <?php echo $selc;?>><?php echo $cls['name'];?></option>                        <?php endforeach;?>                    </select>                </div>            </div>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('session_fee');?></label>                <div class="col-sm-6">                    <input type="text" class="form-control" name="session_fee" value="<?php echo $row['session_fee'];?>"                           data-validate="required" data-message-required="<?php echo get_phrase('value_required');?>"/>                </div>            </div>            <div class="form-group">                <label class="col-sm-3 control-label"><?php echo get_phrase('description');?></label>                <div class="col-sm-6">                    <input type="text" class="form-control" name="description" value="<?php echo $row['description'];?>"/>                </div>            </div>            <div class="form-group">                <div class="col-sm-offset-3 col-sm-5">                    <button type="submit" class="btn btn-info"><?php echo get_phrase('edit_session_fee');?></button>                </div>            </div>            </form>        <?php endforeach;?>    </div></div>