<!DOCTYPE html><html><head>    <meta charset="utf-8">    <link href="https://fonts.googleapis.com/css?family=Open+Sans:400,700" rel="stylesheet">    <link rel="stylesheet" href="<?php echo base_url('assets/css/bootstrap.css');?>">    <link rel="stylesheet" href="<?php echo base_url('assets/css/custom.css');?>"></head>    <body><style>    @page { sheet-size: A4-L; }    .header_igm img{        width: 150px;    }    .header_igm{        text-align: center;        font-size: 21px;        margin-left: -24px;        margin-top: 42px;        font-weight: bold;        color: #000;    }    .header_igm h2{        color: #000!important;        text-align: center;        font-size: 20px;        font-weight: bold !important;    }    .header_igm p {        font-size: 15px;    }    body{        font-size: 12px;    }    table{        width: 100%;    }    table tr th{        text-align: center;    }    table tr td, table tr th{        color: #000;        padding: 5px;    }    .text_align{        text-align: center;    }    .text_align:first-child{        text-align: left!important;    }    .text_align:last-child{        text-align: right!important;    }</style>    <div class="container body_all">        <div class="head_top">            <div class="c_header">                <div class="header_igm col-md-12">                    <img width="100" src="<?php echo base_url('uploads/logo.png') ?>" alt="">                    <h2><?php echo get_settings('system_name'); ?></h2>                    <p>Employee Salary Sheet</p>                    <?php                    $dateObj   = DateTime::createFromFormat('!m', $month);                    $monthName = $dateObj->format('F');                    ?>                    <p><?=$monthName?>, <?=$year1?></p>                </div>            </div>        <div class="clr"></div>        </div>        <div class="salary_body">            <table border="1">                <thead>                    <tr>                        <th>Sl</th>                        <th>Employee Name</th>                        <th>Total Working Dyes</th>                        <th>Total Absent</th>                        <th>Holiday</th>                        <th>Leave</th>                        <th>Payable Days</th>                        <th>Total Salary</th>                        <th>Basic Salary</th>                        <th>Net Salary</th>                    </tr>                </thead>                <tbody>                <?php                $holiday    = ($number-$all_working_days);                $CI = get_instance();                $CI->load->model('report_model');                $sl = 0;                $total_amount = 0;                foreach ($all_employee as $employee):                    $sl++;                    $present_days = $CI->Report_model->get_present_days($employee->teacher_id, $month, $year1, $number);                    $leave = $CI->Report_model->get_employeeLeaveDaysByMonth($employee->teacher_id, $month, $year1);//                    echo $this->db->last_query();//                    var_dump($present_days); exit();                    $absent_days = ($all_working_days-($present_days+$leave));                    $absent_days = ($absent_days < 0) ? 0 : $absent_days;                    $total_payable_days = $all_working_days-$absent_days;                    $basic_pay_per_day = ($employee->basic_salary/30);                    $payable_basic = ($basic_pay_per_day*$total_payable_days);                    $others = ($employee->total_salary-$employee->basic_salary);                    $net_salary = ($payable_basic+$others);                    $net_salary = ($all_working_days==$total_payable_days) ? $employee->total_salary : $net_salary;                    $total_amount +=$net_salary;                ?>                    <tr>                        <td style="text-align: center"><?=$sl?></td>                        <td style="text-align: center"><?=$employee->name?></td>                        <td style="text-align: center; width: 15%"><?=$all_working_days?></td>                        <td style="text-align: center"><?=$absent_days?></td>                        <td style="text-align: center"><?=$holiday?></td>                        <td style="text-align: center"><?=$leave?></td>                        <td style="text-align: center"><?=$total_payable_days?></td>                        <td style="text-align: right"><?= number_format($employee->total_salary,0)?></td>                        <td style="text-align: right"><?= number_format($employee->basic_salary,0)?></td>                        <td style="text-align: right"><?= number_format($net_salary, 0)?></td>                    </tr>                <?php endforeach;?>                    <tr>                        <td colspan="9" style="text-align: right; margin-right: 30px; font-weight: bold">Total =</td>                        <td style="text-align: right; font-weight: bold"><?=number_format($total_amount,0)?></td>                    </tr>                </tbody>            </table>        </div>        <div class="clr"></div>        <br>        <table width="100%" style="margin-top: 50px">            <tr>            <?php            $count= 0;            $i = 0;            $right = count($all_signature);            foreach ($all_signature as $signature):                $count++;            ?>                <td class="text_align" style="font-size: 10px; text-align: <?php if ($count == 1){echo 'left';} elseif ($i == $right -1){'right!important;';} else{ echo 'center';}                ?>"> <span style="border-top: 1px dotted #000; text-transform: uppercase"><?=$signature->name.'</span> <br style="border-top: 0 !important; text-align: center">'.$signature->designation?> <br><p> </p></td>            <?php endforeach;?>            </tr>        </table>        <br>        <br>    </body></html>